import React, {useEffect, useState} from 'react';
import axios from 'axios';
import styled from 'styled-components';
import TeachersList from './Teacher';
import Form from './Form-Search';
import { Link, Route } from 'react-router-dom';
import StudentProfile from './Student-profile';
import Login from './Login';
import './Links.css';
import axiosWithAuth from '../utils/axiosWithAuth';

const Header = styled.h1`
padding:.3rem;
text-shadow: -1.5px -1.5px 0 #003F87, 1.5px 1.5px 0 #003F87, 0 -1.5px 0 #003F87,
0 1.5px 0 #003F87, 1.5px -1.5px 0 #003F87, -1.5px 1.5px 0 #003F87, 1.5px 0 0 #003F87,
-1.5px 0 0 #003F87; 
letter-spacing: 1px;
font-size:2.5rem;
color:#00B2EE;
`

const CustomNav = styled.nav`
display:flex; 
justify-content: space-around;
width: 100%;
`

const CustomizeContainer = styled.div`
display:flex; 
flex-direction: column;
align-items: center;
`

function StudentView(){

const [teachers, setTeachers] = useState([]);
const [displayed, setDisplayed ] = useState([])
const [input, setInput] = useState('');

   useEffect(()=>{
    axiosWithAuth().get('https://cloudschoolbw.herokuapp.com/api/student').then(res => {
        setDisplayed(res.data)
        setTeachers(res.data)
    }).catch(err => console.log(err))
   }, [])
    // const axiosCall = e => {
    //     e.preventDefault();
    //     axiosWithAuth().get('https://cloudschoolbw.herokuapp.com/api/student').then(res => setDisplayed(res.data)).catch(err => console.log(err))
    // }



   const inputChange = e => {
        setInput(e.target.value)
        const filtered = teachers.filter( teacher => (teacher.name.toLowerCase().includes(input.toLowerCase())))
        setDisplayed(filtered)
    }

    return(
        <CustomizeContainer>
            <Header>Welcome Back Name!</Header>
            <CustomNav>
                <Link className='links nestedlink' to='/student'>Profile</Link>
                <Link className='links nestedlink' to='/student/search-teachers'>Search for Teachers</Link>
                <Link className='links nestedlink' to='/'>Logout</Link>
            </CustomNav>
            <Route exact path='/'>
                <Login/>
            </Route>
            <Route exact path='/student/'>
                <StudentProfile/>
            </Route>
            <Route path='/student/search-teachers'>
                <Form inputChange={inputChange}/>
                <TeachersList teachers={displayed} />
            </Route>
            {/* <button onClick={axiosCall}> do axios call</button> */}
        </CustomizeContainer>
    )
}

export default StudentView;
