import React, {useEffect, useState} from 'react';
import axios from 'axios';
import styled from 'styled-components';
import TeachersList from './teachers'
const SearchInput = styled.input`
width:50%;
margin:1rem;
`



function StudentView(){

const [teachers] = useState(['jim', 'tim', 'will', 'bill', 'dylan', 'steph', 'mike', 'ben', 'howard', 'stern', 'jacob', 'alex', 'mark', 'dev']);
const [displayedTeachers , setDisplayedTeachers] = useState([]);
const [input, setInput] = useState('')
    // useEffect(()=>{
    //     axios.get('url').then(res => {
    //         const teacher = res.filter( teacher => {
    //             teacher.name.toLowerCase().includes(Search.toLowerCase())
    //         })
    //                 setUserInfo(res);
    //                 // setToDo(res);
    //             }).catch(err => console.log(err))

    // })
    function inputChange(e){
        setInput(e.target.value);
        console.log(teachers)
        const search = teachers.filter( teacher => {
            teacher.toLowerCase().includes(input.toLowerCase()
        )} )
            setDisplayedTeachers(search)
    }
    return(
        <div>
            <h1>Welcome Name!</h1>
            <form>
                <label htmlFor='search'>
                    <SearchInput onChange={inputChange} className='search' type='text' name='search' id='search'/>
                    Search Teachers
                </label>
            </form>
            <TeachersList teachers={displayedTeachers} />
        </div>
    )
}

export default StudentView;
